function CountTeamsKindOf_BFME2ROTWK(team, kindof) 
	local KindOf_
	if type(kindof) == "number" then KindOf_=kindof
		elseif type(kindof) == "string" then KindOf_=KindOfTable[kindof]
		if KindOf_==nil then KindOf_=KindOfIndexTable[GetIndexOfTableElement(KindOfIndexTable,kindof)-1] end
	end
	local sc=0
	local lower, upper, middle
	upper=1
	while EvaluateCondition("TEAM_HAS_FEWER_THAN_X_UNITS_WITH_KINDOF", team, upper, KindOf_) do
		upper=upper*10
		sc=sc+1 if sc >=100000 then return 0 end
	end
	while not EvaluateCondition("TEAM_HAS_FEWER_THAN_X_UNITS_WITH_KINDOF", team, floor(upper/2), KindOf_) do
		upper=floor(upper/2)
		sc=sc+1 if sc >=100000 then return 0 end
	end
    lower=floor(upper/2)
	while (upper-lower)>1 do 
		middle=(upper+lower)/2
		if not EvaluateCondition("TEAM_HAS_FEWER_THAN_X_UNITS_WITH_KINDOF", team, ceil(middle), KindOf_) then upper=ceil(middle)
		else lower=floor(middle) end
		sc=sc+1 if sc >=100000 then return 0 end
	end   
	middle=ceil((upper+lower)/2)
	if not EvaluateCondition("TEAM_HAS_FEWER_THAN_X_UNITS_WITH_KINDOF", team, middle, KindOf_) then return lower
	else return upper end
end

function CountTeamsKindOf_BFME2ROTWK(team, kindof)
	for i=0,100,1 do
		if not EvaluateCondition("TEAM_HAS_FEWER_THAN_X_UNITS_WITH_KINDOF", team, i, KindOfTable[kindof]) then
			return (i-1)
		end
	end
	return 0
end

CustomHeroRefInit = false
CustomHeroRef = {}
function GetCustomHeroRef(self)
	if not CustomHeroRefInit then
		for k,v in globals() do
			if strfind(tostring(k), "ObjID") ~= nil then
				if strfind(ObjectDescription(v), "CreateAHero") and GetTeamName(v) == GetTeamName(self) then 
					CustomHeroRef = v
					CustomHeroRefInit = true
					return v 
				end
			end
		end	
		return "not found"
	else 
		return CustomHeroRef
	end
end

HeroCustomizationSave = {}

function RefreshHeroCustomizationSave(CreateAHeroPart, state)
	local EntryFound = false
	for i=1,getn(HeroCustomizationSave),1 do
		if HeroCustomizationSave[i][1] == CreateAHeroPart then 
			HeroCustomizationSave[i][2] = state 
			EntryFound = true
			return
		end
	end
	if not EntryFound then
		tinsert(HeroCustomizationSave, { CreateAHeroPart, state } )
	end
end

function ApplyHeroCustomizationSave()
	local HeroRef = GetCustomHeroRef(self)
	for i=1,getn(HeroCustomizationSave),1 do
		ObjectHideSubObjectPermanently(HeroRef, HeroCustomizationSave[i][1], HeroCustomizationSave[i][2]) 
	end
end

function SaveHeroCustomizationSave()
	local SaveData = ""
	for i=1,getn(HeroCustomizationSave),1 do
		if i==1 then SaveData = "HeroCustomizationSave = {" .. [[{"]] .. HeroCustomizationSave[i][1] .. [[{", ]] .. HeroCustomizationSave[i][2] .. "}"
		elseif i==getn(HeroCustomizationSave) then SaveData = SaveData .. [[{"]] .. HeroCustomizationSave[i][1] .. [[{", ]] .. HeroCustomizationSave[i][2] .. "}}"
		else SaveData = SaveData .. [[, {"]] .. HeroCustomizationSave[i][1] .. [[{", ]] .. HeroCustomizationSave[i][2] .. "}, "
		end	
	end
	WriteToFile(SaveData, GetFilePath() .. "HeroCustomizationSave.lua", "w")
end

