function PathExists(PathToTest)
    local tmppath="" .. PathToTest .. "\\" .. tmpname() .. ""
	local filehandle = writeto(tmppath)
	if filehandle ~= nil then closefile(filehandle) end
	local IsPathExistent = rename(tmppath, tmppath)
	remove(tmppath)
	if IsPathExistent then return true	
	else return false end
end

function PathExistsOLD(PathToTest)
    tmppath="" .. PathToTest .. "\\" .. tmpname() .. ""
	writeto(tmppath)
	writeto()
	IsPathExistent = tostring(rename(tmppath, tmppath))
	remove(tmppath)
	if IsPathExistent ~= "nil" then
		return true	
		else 
		return false
	end
end

function FileExists(FilePathToTest)
	local IsFileExistent = rename(FilePathToTest, FilePathToTest)
	if IsFileExistent then return 1	
	else return 0 end
end

function CreateFolder(filepath,foldername)
	if strfind(GetOS(),"Windows") then
		if filepath == nil and foldername ~= nil then execute("START /B /MIN cmd /c mkdir " .. strchar(34) .. GetFilePath() .. "\\" .. foldername ..  strchar(34)  .. " & " .. "START /MAX" .. ProcessName)
		elseif filepath ~= nil and foldername == nil then execute("START /B /MIN cmd /c mkdir " .. strchar(34) .. filepath ..  strchar(34) .. " & " .. "START /MAX" .. ProcessName)
		elseif filepath ~= nil and foldername ~= nil then execute("START /B /MIN cmd /c mkdir " .. strchar(34) .. filepath .. "\\" .. foldername .. "\\" .. strchar(34)  .. " & " .. "START /MAX" .. ProcessName)
		elseif filepath == nil and foldername == nil then execute("START /B /MIN cmd /c mkdir " .. strchar(34) .. GetFilePath() .. "\\" .. RandomString(5) ..  strchar(34)  .. " & " .. "START /MAX" .. ProcessName) 
		end
	else error("OS not compatible, only windows") return "" end
end

