function OpenFolder(filepath)
	if strfind(GetOS(),"Windows") then
		if filepath == nil then execute("explorer " .. strchar(34) .. "" .. GetFilePath() .. "" .. strchar(34) .. "")
		else execute("explorer " .. strchar(34) .. "" .. filepath .. "" .. strchar(34) .. "") end
	else error_("Error in OpenFolder function: OS not compatible, only windows") end
end

function ExtractRegistryValue(REGKEY,REGVAL)
	local REGISTRY_VALUE	
	local TransferFile = "USERDATAPATH.TXT"
	if not rename(TransferFile, TransferFile) then
		local REG_QUERY_CMD = [[CHCP 1252 & REG QUERY "]] .. REGKEY .. [[" /v "]] .. REGVAL .. [[" > ]] .. TransferFile
		execute(REG_QUERY_CMD)
		if ProcessName ~= nil then
			--execute("START /MAX" .. ProcessName)
		end
		local i=clock()+0.1
		while(clock()<i) do end
	end
	local filehandle = openfile(TransferFile, 'r')
	local filesize = seek(filehandle, "end")
	seek(filehandle, "set")
	if filesize ~= nil and filesize ~= 0 then	
		local line = read(filehandle, '*l')
		while line ~= nil do
			if strfind(line, "REG_SZ") then 
				REGISTRY_VALUE = strsub(line, 19+strlen(REGVAL))
			end
			line = read(filehandle, '*l')
		end
	end
	closefile(filehandle)
	--remove("REGVAL.txt")
	--print(REGISTRY_VALUE)
	return REGISTRY_VALUE
end	

function ExtractRegistryValueAndDeleteTransferFile(REGKEY,REGVAL)
	local REGISTRY_VALUE
	local REG_QUERY_CMD = [[CHCP 1252 & REG QUERY "]] .. REGKEY .. [[" /v "]] .. REGVAL .. [[" > REGVAL.txt]]
	print(REG_QUERY_CMD)
	execute(REG_QUERY_CMD)
	sleep(0.1)
	local filehandle = openfile("REGVAL.txt", 'r')
	local filesize = seek(filehandle, "end")
	seek(filehandle, "set")
	if filesize ~= nil and filesize ~= 0 then	
		local line = read(filehandle, '*l')
		while line ~= nil do
			if strfind(line, "REG_SZ") then 
				REGISTRY_VALUE = strsub(line, 19+strlen(REGVAL))
			end
			line = read(filehandle, '*l')
		end
	end
	closefile(filehandle)
	remove("REGVAL.txt")
	return REGISTRY_VALUE
end	

