function GetObjectTypeListOfKindOfList(ObjectTypeTable,kindoflist)
	local TempTeam=CivilianTeam
	local ResultTable={}
	local TREF = ""
	local kindofcounter1=0
	local kindofcounter2=0
	local MatchCountTable = {}
	for i=1,getn(ObjectTypeTable),1 do
		MatchCountTable[ObjectTypeTable[i]] = 0
	end
	for j=1,getn(kindoflist),1 do
		for i=1,getn(ObjectTypeTable),1 do
			if MatchCountTable[ObjectTypeTable[i]] == j-1 or j==1 then
				if ObjectTypeTable[i]~="DwarvenMagicDoor" and ObjectTypeTable[i]~="WildMineShaft" then
					TREF=spawn(ObjectTypeTable[i],TempTeam)
					kindofcounter1=CountTeamsKindOf(TempTeam,kindoflist[j])
					ExecuteAction("UNIT_SET_TEAM",TREF,NeutralTeam)
					kindofcounter2=CountTeamsKindOf(TempTeam,kindoflist[j])
					delete(TREF)
					if kindofcounter1~=kindofcounter2 then 
						MatchCountTable[ObjectTypeTable[i]]=MatchCountTable[ObjectTypeTable[i]]+1
						if MatchCountTable[ObjectTypeTable[i]]==getn(kindoflist) then
							tinsert(ResultTable,ObjectTypeTable[i]) 
						end
					end
				end
			end
		end
	end
	return ResultTable
