function GetCounterValueFast(counter)
	local CounterRefLowerLimit = RandomString(3)
	local CounterRefUpperLimit = CounterRefLowerLimit .. "_Upper"
	local CounterRefMiddle = CounterRefLowerLimit .. "_Middle"
	local LowerValue = 0
	local UpperValue = 1
	if EvaluateCondition("COUNTER_COUNTER", counter, CompareTable["EQ"], CounterTable[0]) then return 0 end
	ExecuteAction("COUNTER_MATH_COUNTER", CounterRefUpperLimit, MathOpTable["Add"], CounterTable[1])
	ExecuteAction("SET_COUNTER", CounterRefLowerLimit, 0)	
	while EvaluateCondition("COUNTER_COUNTER", counter, CompareTable["GE"], CounterRefUpperLimit) do 
		ExecuteAction("COUNTER_MATH_COUNTER", CounterRefUpperLimit, MathOpTable["Multiply"], CounterTable[10])	
		UpperValue = UpperValue *10
	end
	local v = {}
	v.GetCounterValue = function(LowerLimitValue, UpperLimitValue, LowerLimitCounter, UpperLimitCounter)
		if (UpperLimitValue - LowerLimitValue) <=1 then 
			--ExecuteAction("COUNTER_MATH_COUNTER", LowerLimitCounter, MathOpTable["Subtract"], CounterTable[1])
			if EvaluateCondition("COUNTER_COUNTER", %counter, CompareTable["EQ"], LowerLimitCounter) then return LowerLimitValue
				else return LowerLimitValue - 1
			end
		end
		local MiddleCounter = RandomString(3)
		ExecuteAction("SET_COUNTER", MiddleCounter, 0)
		ExecuteAction("COUNTER_MATH_COUNTER", MiddleCounter, MathOpTable["Add"], LowerLimitCounter)
		ExecuteAction("COUNTER_MATH_COUNTER", MiddleCounter, MathOpTable["Add"], UpperLimitCounter)
		ExecuteAction("COUNTER_MATH_COUNTER", MiddleCounter, MathOpTable["Divide"], CounterTable[2])
		local MiddleValue = (LowerLimitValue + UpperLimitValue)/2			
		if EvaluateCondition("COUNTER_COUNTER", %counter, CompareTable["<="], MiddleCounter) then return %v.GetCounterValue(LowerLimitValue,MiddleValue,LowerLimitCounter,MiddleCounter)
		else return round(%v.GetCounterValue(MiddleValue,UpperLimitValue,MiddleCounter,UpperLimitCounter)) end
	end
	v.GetExactCounterValue = function(ApproxValue,AppoxCounter)
		ExecuteAction("COUNTER_MATH_COUNTER", AppoxCounter, MathOpTable["Subtract"], CounterTable[10])	
		for i=1,20,1 do
			ExecuteAction("COUNTER_MATH_COUNTER", AppoxCounter, MathOpTable["Add"], CounterTable[1])
			if EvaluateCondition("COUNTER_COUNTER", %counter, CompareTable["EQ"], AppoxCounter) then return (ApproxValue-10+i) end
		end
		return ApproxValue
	end
	return v.GetCounterValue(LowerValue,UpperValue,CounterRefLowerLimit,CounterRefUpperLimit)
end

