function GetCounterValue(counter)
	local refCounter = "counter_" .. floor(GetRandomNumber()*99999999+0.5)
	ExecuteAction("SET_COUNTER", refCounter, 0)
	if EvaluateCondition("COUNTER_COUNTER", counter, CompareTable["EQ"], CounterTable[0]) then 
		return 0 
	end
	local value = 1
	ExecuteAction("COUNTER_MATH_COUNTER", refCounter, MathOpTable["Add"], CounterTable[1])
	ExecuteAction("SET_COUNTER", CounterRefLowerLimit, 0)	
	while EvaluateCondition("COUNTER_COUNTER", counter, CompareTable["GE"], refCounter) do 
		ExecuteAction("COUNTER_MATH_COUNTER", refCounter, MathOpTable["Multiply"], CounterTable[10])	
		value = value * 10
		if value > 100000000 then return "error1" end
	end
	while EvaluateCondition("COUNTER_COUNTER", counter, CompareTable["NE"], refCounter) do	 
		ExecuteAction("COUNTER_MATH_COUNTER", refCounter, MathOpTable["Subtract"], CounterTable[1])
		value = value - 1
		if value < 0 then return "error1" end
	end
	return value
end

