		--AdminTeamName=GetTeamName(self)
		HandleAutoconfig()
		if Hero_General_Gamemode ~= 1 then ExecuteAction("MOVIE_PLAY_RADAR", "METAMODINTRO") end
		if ( (AutoConfig_General == 0) or (AutoConfig_Skirmish == 0 and Human_Players_Count == 1) or (AutoConfig_Multiplayer == 0 and Human_Players_Count > 1) or (AutoConfig_StoryMode == 0 and (IsGameInStoryMode(self)==1 or TestCampaignFlag()==1)) or (AutoConfig_GlobalConquestMode == 0 and IsMetaGameActive() == 1) ) then VanillaSettings() return end    --tostring(EvaluateCondition("IS_GAME_IN_SKIRMISH_OR_MULTIPLAYER", 1))
		--object1 = getglobal(GetObj.Index(self))
		--PeriodicMasterCheck()	
		--spawn("MetaControlDummyPeriodicMasterChecker")	
		--ExecuteAction("CREATE_UNNAMED_ON_TEAM_AT_WAYPOINT", "MetaControlDummyPeriodicMasterChecker", NeutralTeam, "Player_1_Start")	
		if Dev_Mode == 1 then
			ErrorWarnings=1
			ErrorLogToFile=1
