	--push(MoveMode1)   
	local MoveMode2 = function() ExecuteAction("UNIT_GUARD_OBJECT",%Object,%alpha) pop() end
	push(MoveMode2)
	--local DeleteDummy = function() ExecuteAction("NAMED_DELETE",%alpha) pop() end
	--push(DeleteDummy)
end


