	NAOD.NextRoundIndicator = 1
	NAOD.FollowWaypointCommandIssued = false
	NAOD.PreAttackActionsIssued = false
	NAOD.TeamObjectListRefreshed=false
	NAOD.NativeArtOfDefenseMain()   
end                                                          
